document.addEventListener("DOMContentLoaded",()=>{console.log("Tycoon Brief landing page loaded.");const expertCards=document.querySelectorAll(".expert-card");expertCards.forEach(card=>{card.addEventListener("click",function(){this.classList.toggle("active");expertCards.forEach(otherCard=>{if(otherCard!==this&&otherCard.classList.contains("active")){otherCard.classList.remove("active")}})})});const emailForm=document.querySelector(".email-form");const emailInput=emailForm.querySelector('input[type="email"]');const requestButton=emailForm.querySelector(".request-button");const fbLeadForm=document.getElementById("fb-lead-form");fbLeadForm.addEventListener("submit",function(e){e.preventDefault();if(emailInput.value){requestButton.textContent="Processing...";requestButton.disabled=true;if(typeof fbq!=="undefined"){fbq("track","Lead",{content_name:"Tycoon Brief Signup",content_category:"Intelligence Brief Subscription",value:0,currency:"USD"})}fetch(fbLeadForm.action,{method:"POST",body:new FormData(fbLeadForm),mode:"no-cors"}).then(()=>{console.log("Form submitted successfully")}).catch(error=>{console.error("Error submitting form:",error)}).finally(()=>{setTimeout(()=>{requestButton.textContent="Access Granted!";requestButton.style.backgroundColor="#2ecc71";emailInput.value="";setTimeout(()=>{requestButton.textContent="Request Access";requestButton.style.backgroundColor="#cc3333";requestButton.disabled=false},3e3)},1500)})}else{alert("Please enter a valid email address.")}})});